FROM SURVEY.SURVEY_ANS_MST
WHERE SUFRER_PIN = #{sufrerPin} AND SUFRER_NM = #{sufrerNm} AND SEXDSTN = #{sexdstn} AND BRTHDY = #{brthdy}


